SELECT
TABLE_NAME,CHAPTER_CODE,CHAPTER_STATER,CHAPTER_ID,CHAPTER_NAME,PROFESSIONAL_TYPE_CODE,PROFESSIONAL_NAME,COURSE_TYPE_CODE,COURSE_NAME,CHAPTER_NAME_CODE,CLASSS_TIME,TRAINER,TRAINER_PHONE,CHAPTER_OFFICE,CHAPTER_DESCRIBE,TEST_WAY,OVER_TIME,POST_ID,UP_DATE,COURSE_TYPE_NAME
    FROM chapter_tab where COURSE_TYPE_NAME like '%统计监测%' and CHAPTER_STATER = '已审核'

		AND CHAPTER_OFFICE = #{chapterOffice}


		AND COURSE_TYPE_NAME = #{courseTypeName}


		AND PROFESSIONAL_NAME = #{professionalName}


	    AND COURSE_NAME like CONCAT(CONCAT('%', #{courseName}), '%')


	AND TABLE_NAME like CONCAT(CONCAT('%', #{tableName}), '%')


	AND JURIS_TYPE like CONCAT(CONCAT('%', #{jurisType}), '%')

SELECT count(*)
    FROM chapter_tab  where COURSE_TYPE_NAME like '%统计监测%' and CHAPTER_STATER = '已审核'

		AND CHAPTER_OFFICE = #{chapterOffice}


		AND COURSE_TYPE_NAME = #{courseTypeName}


		AND PROFESSIONAL_NAME = #{professionalName}


	    AND COURSE_NAME like CONCAT(CONCAT('%', #{courseName}), '%')


	    AND TABLE_NAME like CONCAT(CONCAT('%', #{tableName}), '%')


	AND JURIS_TYPE like CONCAT(CONCAT('%', #{jurisType}), '%')


